import React from 'react';
import './App.css';
import Context from './Context';
import allSupers from './assets/characters-corrupt.json'; 
import ShowAll from './components/ShowAll';
import Compare from './components/Compare';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  // Link,
  // useRouteMatch,
  // useParams
} from "react-router-dom";

class App extends React.Component {
  constructor(props) {
    super(props)

    this.state = {data: allSupers};
  }
  render() {

    return (
      <Context.Provider value={this.state}>
    <Router>
      <div>
        {/* <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/showall">Show All Heroes</Link>
          </li>
          <li>
            <Link to="/compare">Compare Heroes</Link>
          </li>
        </ul> */}

        <Switch>
          <Route path="/about">
            <ShowAll />
          </Route>
          <Route path="/topics">
            <Compare />
          </Route>
          <Route path="/">
            <App />
          </Route>

        </Switch>
      </div>
    </Router>


      </Context.Provider>
    );
  }
}
export default App;
