import React from 'react'
import Context from '../Context.js'

class Supershow extends React.Component {
    static contextType = Context;

    constructor(props) {
        super(props);
        this.state = {hero: props.id}; 
    }

    render() {
        debugger
        let value = this.context.data.filter(item=> item.id === this.state.hero)[0];

        let alignment = value.biography.alignment.toUpperCase();

        let image_url = value.images.xs;

        let name = value.biography.fullName;



return (  

  <div className="image">
    <h2>{name}</h2> 
    <img src={image_url} alt="hero image xs"></img>

      <h1>{alignment}</h1>
    {/* <h4>feels like {feels}Â° </h4> */}
  </div>
  )

}
}

export default Supershow
