import React from 'react';
import './App.css';
import Context from './Context';
import allSupers from './assets/characters-corrupt.json'; 
import Supershow from './components/Supershow';

class App extends React.Component {
  constructor(props) {
    super(props)

    // this.getData = (newData) => {
    //   this.setState(state => ({
    //     data: newData
    //   }));
    // };

    // State also contains the updater function so it will
    // be passed down into the context provider
    this.state = {data: allSupers};
  }

  // componentDidMount() {
  //     debugger
  // }

  render() {
    console.log(this.state);

    let alphaSupers = this.state.data.sort((a,b)=>{
      if (a.biography.fullName > b.biography.fullName) {
        return -1;
    }
    if (b.biography.fullName > a.biography.fullName) {
        return 1;
    }
    return 0;

    })

    let supers = alphaSupers.map((value, index)=>{
      return <Supershow id={value.id} key={index} />
    })


    return (
      <Context.Provider value={this.state}>

        {supers}
      </Context.Provider>
    );
  }
}
export default App;
