import React from 'react';
import './App.css';
import Context from './Context';
import allSupers from './assets/characters-corrupt.json'; 
import Supershow from './components/Supershow';

class App extends React.Component {
  constructor(props) {
    super(props)

    // this.getData = (newData) => {
    //   this.setState(state => ({
    //     data: newData
    //   }));
    // };

    // State also contains the updater function so it will
    // be passed down into the context provider
    this.state = {data: allSupers};
  }

  // componentDidMount() {
  //     debugger
  // }

  render() {
    console.log(this.state);

    let goodGuys = this.state.data.filter(item=> item.biography.alignment.toLowerCase() === "good");
    let badGuys = this.state.data.filter(item=> item.biography.alignment.toLowerCase() === "bad");
    let unknownGuys =  this.state.data.filter(item=> !item.biography.alignment);


    let alphaSupers = goodGuys.sort((a,b)=>{
      if (a.biography.name.toLowerCase() > b.biography.name.toLowerCase()) {
        return 1;
    }
    if (b.biography.name.toLowerCase() > a.biography.name.toLowerCase()) {
        return -1;
    }
    return 0;

    })

    let alphaVillains = badGuys.sort((a,b)=>{
      if (a.biography.name.toLowerCase() > b.biography.name.toLowerCase()) {
        return 1;
    }
    if (b.biography.name.toLowerCase() > a.biography.name.toLowerCase()) {
        return -1;
    }
    return 0;

    })

    let alphaRest = unknownGuys.sort((a,b)=>{
      if (a.biography.name.toLowerCase() > b.biography.name.toLowerCase()) {
        return 1;
    }
    if (b.biography.name.toLowerCase() > a.biography.name.toLowerCase()) {
        return -1;
    }
    return 0;

    })

    let supers = alphaSupers.map((value, index)=>{
      return <Supershow id={value.id} key={index} />
    })

    let villains = alphaVillains.map((value, index)=>{
      return <Supershow id={value.id} key={index} />
    })

    let unknown = alphaRest.map((value, index)=>{
      return <Supershow id={value.id} key={index} />
    })

    return (
      <Context.Provider value={this.state}>

        <div> 
          {supers}
        </div> 
        <div>

        </div>
          {villains}
        <div>
          {unknown} 

        </div>

      </Context.Provider>
    );
  }
}
export default App;
