import React from 'react';
import './App.css';
import Context from './Context';
import allSupers from './assets/characters-corrupt.json'; 
import Supershow from './components/Supershow';

class App extends React.Component {
  constructor(props) {
    super(props)

    // this.getData = (newData) => {
    //   this.setState(state => ({
    //     data: newData
    //   }));
    // };

    // State also contains the updater function so it will
    // be passed down into the context provider
    this.setState({data: allSupers});
  }

  componentDidMount() {
      // this.getData(allSupers);
      debugger
  }

  render() {
    let supers = this.state.data.map((value, index)=>{
      return <Supershow id={value.id} key={index} />
    })


    return (
      <Context.Provider value={this.state}>

        {supers}
      </Context.Provider>
    );
  }
}
export default App;
